import { Grid, useMediaQuery, useTheme, Card, Box ,CardActions,Button, CardActionArea,CardMedia,CardContent,Typography,Rating} from '@mui/material'
import { Container } from '@mui/system';
import React from 'react'
import Header from '../Header&Footer/Header'
import Footer from '../Header&Footer/Footer';
export const products = [
  {
      id: 1,
      name: "Super Backpack",
      price: 129.99,
      description: "Torem ipsum dolor sit amet, consectetur adipisicing elitsed do eiusmo tempor incididunt ut labore et dolore magna",
      image: "/images/products/bag_01.png",
      rating:3
  },
  {
      id: 2,
      name: "New Hip",
      price: 199.99,
      description: "Torem ipsum dolor sit amet, consectetur adipisicing elitsed do eiusmo tempor incididunt ut labore et dolore magna",
      image: "/images/products/bag_02.png",
      rating:4,
  },
  {
      id: 3,
      name: "Elite Series",
      price: 189.99,
      description: "Torem ipsum dolor sit amet, consectetur adipisicing elitsed do eiusmo tempor incididunt ut labore et dolore magna",
      image: "/images/products/bag_03.png",
      rating:2
  },
  {
      id: 4,
      name: "Casual",
      price: 129.99,
      description: "Torem ipsum dolor sit amet, consectetur adipisicing elitsed do eiusmo tempor incididunt ut labore et dolore magna",
      image: "/images/products/bag_04.png",
      rating:5
  },
  {
      id: 5,
      name: "Best Tote",
      price: 399.99,
      description: "Torem ipsum dolor sit amet, consectetur adipisicing elitsed do eiusmo tempor incididunt ut labore et dolore magna",
      image: "/images/products/bag_05.png",
      rating:3
  },
  {
      id: 6,
      name: "Charming Series",
      price: 689.99,
      description: "Torem ipsum dolor sit amet, consectetur adipisicing elitsed do eiusmo tempor incididunt ut labore et dolore magna",
      image: "/images/products/bag_06.png",
      rating:5
  }
];

function Product() {
   const theme=useTheme;
   // const isMatch=useMediaQuery(theme.breakpoints.down("md"));
   
  return (
    <React.Fragment>
       <Header/>
       <Grid container spacing={1} sx={{marginTop:10}}>{products.map((product,index)=>{
        return(
       
        <Grid item key={index} id={"productGrid"+(index+1)}  xs={3}>
        <Card sx={{ maxWidth: 345 }}>
      <CardActionArea>
        <CardMedia
          component="img"
          height="140"
          image={product.image}
          alt="Loading"
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="div">
            {product.name}
          </Typography>
          <Typography variant="body2" color="text.secondary">
           {product.description}
          </Typography>
          <Typography variant="body2" color="text.secondary">
           {"RS : "+product.price}
          </Typography>
        </CardContent>
      </CardActionArea>
      <CardActions>
      <div>
          <Button aria-label="edit"  id="buyProduct" variant='contained'>
              <span className="material-icons">Buy</span>
          </Button>
      </div>
      <div>
          <Button aria-label="edit"  id="cartProduct" variant='contained'>
              <span className="material-icons">Add to Cart</span>
          </Button>
      </div>
      <Rating name="read-only" value={product.rating} sx={{marginLeft:'auto'}} readOnly />

  </CardActions>
    </Card>
        </Grid>
        );
        }
       )}
       </Grid>
       <Footer/>
    </React.Fragment>
  )
}

export default Product
