import React,{useState} from 'react';
import './Login.css';
import { useStateValue } from "../../../functions/Utils/StateProvider";
import TextField from '@mui/material/TextField';
import InputAdornment from '@mui/material/InputAdornment';

import IconButton from '@mui/material/IconButton';
import FormControl from '@mui/material/FormControl';
import Button from '@mui/material/Button';
import ButtonGroup from '@mui/material/ButtonGroup';
import axios from 'axios';
import Alert from '@mui/material/Alert';
import { validateUser } from '../../../functions/Auth/Login';
import { useNavigate } from 'react-router-dom';
import { actionTypes } from "../../../functions/Utils/Reducer";
import Header from '../../Header/Header';
import LoginHeader from '../../Header/LoginHeader';


function Login() {

  const [state, dispatch] = useStateValue();
  const [values, setValues] = useState({
    username: '',
    password: '',
    errorMsg: '',
    showPassword: false,
    showError: false
  });
  let navigate = useNavigate();

  const performLogin = () => {
    if(values.username === '' || values.password === ''){
      setValues({...values, showError: true, errorMsg: 'Required Fields Missing'})
      setTimeout(() => {
        setValues({...values, showError: false, errorMsg: ''})
      }, 3000);
    }else{
      const res = validateUser(values.username, values.password)
      if(res.status){
        dispatch({
          type: actionTypes.SET_USER,
          user: res.userID,
          userType : res.userType
        });
        res.userType === 'admin' ? navigate('/admin') : navigate('/')
      }else{
        setValues({...values, showError: true, errorMsg: 'Invalid Username/Password'})
        setTimeout(() => {
          setValues({...values, showError: false, errorMsg: ''})
        }, 3000);
      }
    }
  }

  const handleChange = (prop) => (event) => {
    setValues({ ...values, [prop]: event.target.value });
  };

  const handleClickShowPassword = () => {
    setValues({
      ...values,
      showPassword: !values.showPassword,
    });
  };

  return (
    <React.Fragment>
      <LoginHeader/>
    <div className='Login-Container'>
        <div className='Login-Wrapper'>
            <div className='Login-Left'>
                <img className='Login-Image'/>
            </div>

            <div className="Login-Right">
            <h2 className="Login-Header">Login</h2>
            {
            values.showError ? (
              <Alert severity="error" className="Login-Error">
                Error — <strong>{values.errorMsg}</strong>
              </Alert>
            ) : (
              <div style={{marginBottom:"45px"}}></div>
            )
          }
          <TextField
            error = {values.showError}
            id="email"
            className="Login-Input"
            label="Username *"
            value={values.username}
            onChange={handleChange('username')}
            variant="standard"
          />
            <div className="bloc-tabs">
              <ButtonGroup className='user-change-btn'>
                    <Button
                        //className={toggleState === 1 ? "tabs active-tabs" : "tabs"}
                        id="userTab"
                       // onClick={() => toggleTab(1)}
                      >
                      USER
                      </Button>
                      <Button
                       // className={toggleState === 2 ? "tabs active-tabs" : "tabs"}
                        id="adminTab"
                       //onClick={() => toggleTab(2)}
                      >
                      ADMIN
                      </Button>
                      </ButtonGroup>
                    </div>
                    
          
            <FormControl className="Login-Input" variant="standard">
            <TextField id="email" htmlFor="email" label="UserName" variant='standard'
            required
               endAdornment={
                <InputAdornment position="end">
                  <IconButton
                    //aria-label="toggle password visibility"
                   // onClick={handleClickShowPassword}
                   // onMouseDown={(e) => {e.preventDefault()}}
                  >
                    {/*values.showPassword ? <span className="material-icons">visibility_off</span> : <span className="material-icons">visibility</span>*/}
                  </IconButton>
                </InputAdornment>
              }
            />
              
              <TextField id="password" htmlFor="password" label="Password" variant='standard'
              required
              type="password"
               endAdornment={
                <InputAdornment position="end">
                  <IconButton
                    //aria-label="toggle password visibility"
                   // onClick={handleClickShowPassword}
                   // onMouseDown={(e) => {e.preventDefault()}}
                  >
                    {/*values.showPassword ? <span className="material-icons">visibility_off</span> : <span className="material-icons">visibility</span>*/}
                  </IconButton>
                </InputAdornment>
              }
            />
          </FormControl>
         <p> <Button id="loginButton" variant="contained" className="Login-Button" onClick={()=>alert("Login Successfully")}>Login</Button></p>
          <p className="Login-Text">Don't have an account? <a id="signupLink" href="/user/signup" className="Login-Link">Signup</a></p>
        
        </div>
        </div>
    </div>
    </React.Fragment>
  )
}

export default Login;
